mapper.insert:
INSERT INTO TB_DEPT(
                    DNO
                   , DNAME
                   , LOC
                   , INSERT_TIME)
VALUES (
        SQ_DEPT.NEXTVAL
        , #{dname}
        , #{loc}
        , #{insertTime}
       )

mapper.update:
UPDATE TB_DEPT
SET DNAME      = #{dname}
  , LOC         = #{loc}
  , UPDATE_TIME = #{updateTime}
WHERE DNO = #{dno}

mapper.select:
SELECT COUNT(dno)
FROM TB_DEPT
WHERE DNO = #{dno}
