mapper.insert:
INSERT INTO TB_EMP ( ENO
                   , ENAME
                   , JOB
                   , MANAGER
                   , HIREDATE
                   , SALARY
                   , COMMISSION
                   , DNO
                   , INSERT_TIME)
VALUES ( SQ_EMP.NEXTVAL
       , #{ename}
       , #{job}
       , #{manager}
       , #{hiredate}
       , #{salary}
       , #{commission}
       , #{dno}
       , #{insertTime})

mapper.update:
UPDATE TB_EMP
SET ENAME       = #{ename}
  , JOB         = #{job}
  , MANAGER     = #{manager}
  , HIREDATE    = #{hiredate}
  , SALARY      = #{salary}
  , COMMISSION  = #{commission}
  , DNO         = #{dno}
  , UPDATE_TIME = #{updateTime}
WHERE ENO = #{eno}

mapper.select:
SELECT COUNT(eno)
FROM TB_EMP
WHERE ENO = #{eno}
